$(function(){bind_smartPush(),bind_description(),disable_toggle_button()});var description_default_height=0,smartPush=function(a){$.ajax({url:"/registNewFeed",async:!0,type:"POST",data:{url:a},dataType:"json"}).done(function(a){null!==a&&$("#feed-list-unread").append('<p><a class="unread" href="/feed/'+a.id+'" >'+a.title[0]+"</a></p>")}).fail(function(){})},toggle_description=function(a){desc=a.parents(".list-item").find(".description")[0],$(desc).animate(description_default_height<desc.scrollHeight&&desc.scrollHeight!=desc.offsetHeight?{height:desc.scrollHeight}:{height:description_default_height})},disable_toggle_button=function(){for(var a=$(".list-item .description"),b=0;b<a.length;b++)a[b].scrollHeight==a[b].offsetHeight&&$($(a[b]).parents(".list-item").find("button")[0]).remove()},bind_smartPush=function(){$(".smart-push").click(function(){return smartPush($(this).attr("href")),!1})},bind_description=function(){description_default_height=$(".list-item .description").height(),$(".info button").click(function(){toggle_description($(this))})};